const state = {
    message: '',
    type: ''
  };
  
  const mutations = {
    SET_FLASH_MESSAGE(state, { message, type }) {
      state.message = message;
      state.type = type;
    },
    CLEAR_FLASH_MESSAGE(state) {
      state.message = '';
      state.type = '';
    }
  };
  
  const actions = {
    setFlashMessage({ commit }, { message, type = 'alert-info' }) {
      commit('SET_FLASH_MESSAGE', { message, type });
      setTimeout(() => {
        commit('CLEAR_FLASH_MESSAGE');
      }, 9000); // Clear the message after 9 seconds
    },
    clearFlashMessage({ commit }) {
      commit('CLEAR_FLASH_MESSAGE');
    }
  };
  
  const getters = {
    flashMessage: state => state.message,
    flashType: state => state.type
  };
  
  export default {
    state,
    mutations,
    actions,
    getters
  };
