export default {
  namespaced: true,
    state: {
      message: '',
      type: ''
    },
    getters: {
      flashMessage(){
        return state.message
      },
      flashType(){
        return state.type
      }
    },
    mutations: {
      SET_FLASH_MESSAGE(state, { message, type }) {
        state.message = message;
        state.type = type;
      },
      CLEAR_FLASH_MESSAGE(state) {
        state.message = '';
        state.type = '';
      }
    },
    actions: {
      setFlashMessage({ commit }, payload) {
        // console.log(payload)
        // commit('SET_FLASH_MESSAGE', { message, type });
        // setTimeout(() => {
        //   commit('CLEAR_FLASH_MESSAGE');
        // }, 9000); // Clear the message after 9 seconds
      },
      
      clearFlashMessage({ commit }) {
        commit('CLEAR_FLASH_MESSAGE');
      }
    },
}
